import React, { Component } from "react";
import { connect } from "react-redux";
import { toggleSelect } from "../store/Actions/index";

class HotelsCard extends Component {
  constructor(props){
    super(props)
    this.state= {
      value:''
    }
    this.getAlert = this.getAlert.bind(this);

  }
  clicked(data, e) {
    var obj = { data: data, active: e.target.checked };
    this.props.handlerClick(obj);
  }
  componentDidMount() {
    this.props.setClick(this.getAlert);
 }
 handleChange = (event) =>{
 debugger;
 this.setState({value: event.target.value}) };

 getAlert() {
   debugger;
    console.log('gat value ',this.state.value)
 }
  render() {
    console.log(this.props.adults);

    return (
      <div className="col-md-3">
        <div className="card p-3 mb-2">
          <div className="row">
            {this.props.adults.checkbox ? (
              <div className="form-check">
                {this.props.adults.active ? (
                  <input
                    type="checkbox"
                    className="form-check-input"
                    id="roomCheck1"
                    checked
                    onClick={this.clicked.bind(this, this.props.adults)}
                  />
                ) : (
                  <input
                    type="checkbox"
                    className="form-check-input"
                    id="roomCheck1"
                    onClick={this.clicked.bind(this, this.props.adults)}
                  />
                )}
              </div>
            ) : (
              " "
            )}
            <label className="heading_text">{this.props.adults.name}</label>

            <div className="col">
              <label>Adults (18+)</label>
              {this.props.adults.active ? (
                <select value={this.state.value} onChange={this.handleChange}
                >
                  {[...Array(2)].map((e, i) => {
                    return (
                      <option 
                        selected={this.props.adults.adult == i + 1}
                        
                        key={i}
                        value={i + 1}
                      >
                        {i + 1}
                      </option>
                    );
                  })}
                </select>
              ) : (
                <div>
                  {" "}
                  <select value={this.state.value} onChange={this.handleChange}>
                    {[...Array(2)].map((e, i) => {
                      return (
                        <option
                          selected={this.props.adults.adult == i + 1}
                          key={i}
                          value={i + 1}
                        >
                          {i + 1}{" "}
                        </option>
                      );
                    })}
                  </select>
                </div>
              )}
            </div>

            <div className="col">
              <label>Children (0 - 17)</label>

              {this.props.adults.active ? (
                <select>
                  {[...Array(2)].map((e, i) => {
                    return (
                      <option selected={this.props.adults.children} key={i}>
                        {i}{" "}
                      </option>
                    );
                  })}
                </select>
              ) : (
                <div>
                  {" "}
                  <select disabled>
                    {[...Array(2)].map((e, i) => {
                      return (
                        <option selected={this.props.adults.children} key={i}>
                          {i}{" "}
                        </option>
                      );
                    })}
                  </select>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  console.log(state);
  return {
    adult: state.data
  };
};

export default connect(mapStateToProps)(HotelsCard);

